import React, { Component } from "react";
import { withRouter } from "react-router";

import { connect } from "react-redux";
import { Flex, Box, Button, Heading, Text, IconButton } from "@chakra-ui/core";
import Comment from "../../Comment/Comment";

class PostItem extends Component {
  state = {
    postID: this.props.postItem.id,
  };

  // addPostLike = () => {
  //   //
  // };

  // Axios.delete(`/api/posts/${event.target.id}`)

  deletePost = (event) => {
    console.log("In delete");
    this.props.dispatch({ type: "DELETE_POST", payload: event.target.id });
    try {
      this.props.dispatch({ type: "FETCH_POSTS" });
    } catch (error) {
      console.log(error);
    }
    this.props.history.push("/");
  };

  addPostComment = (event) => {};

  addPostLike = (event) => {};

  editPost = (event) => {};

  render() {
    const { postItem, user } = this.props;

    if (user.id === postItem.user_id) {
      return (
        <Flex width="full" justifyContent="center">
          <Box
            m={4}
            textAlign="center"
            borderWidth={1}
            borderRadius="lg"
            px={4}
            width="full"
            maxWidth="100%"
            textAlign="center"
            boxShadow="lg"
            p={4}
            py={8}
          >
            {/* <div className="postItem"> */}
            <Heading as="h3" size="lg" mb={2}>
              {postItem.post_title}
            </Heading>
            <Text>By</Text>
            <Heading mb={2} as="h5" size="sm">
              {postItem.first_name}
            </Heading>
            <Text mb={8}>{postItem.post_body}</Text>
            <Text>{postItem.likes} Likes</Text>

            <Button m={1} id={postItem.id} onClick={this.addPostLike}>
              Like
            </Button>
            <Button m={1} id={postItem.id} onClick={this.editPost}>
              Edit
            </Button>
            <Button m={1} id={postItem.id} onClick={this.deletePost}>
              Delete
            </Button>
            <Comment postItem={postItem} user={user} />
          </Box>
        </Flex>
      );
    } else {
      return (
        <Flex width="full" justifyContent="center">
          <Box
            m={4}
            textAlign="center"
            borderWidth={1}
            borderRadius="lg"
            px={4}
            width="full"
            maxWidth="100%"
            textAlign="center"
            boxShadow="lg"
            p={4}
            py={8}
          >
            <Heading as="h3" size="lg">
              {postItem.post_title}
            </Heading>
            <Text>By</Text>
            <Heading mb={2} as="h5" size="sm">
              {postItem.first_name}
            </Heading>
            <Text>{postItem.post_body}</Text>
            <p>{postItem.likes}</p>
            <Button m={1} id={postItem.id} onClick={this.addPostLike}>
              Like
            </Button>
            {/* <Comment postItem={postItem} user={user} /> */}
          </Box>
        </Flex>
      );
    }
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
  members: state.members,
});

export default withRouter(connect(mapStateToProps)(PostItem));
