import React, { Component } from "react";
import { withRouter } from "react-router";

import { connect } from "react-redux";
import { Flex, Box, Button, Text, Heading } from "@chakra-ui/core";
import AddComment from "../../Comment/AddComment";
import Axios from "axios";

class ResourceItem extends Component {
  state = {
    comments: {
      comments: 0,
    },
    likes: 0,

    displayThread: false,
  };

  componentDidMount() {
    this.updateComments();
    this.updateLikes();
  }

  updateLikes = () => {
    this.setState({
      likes: this.props.resourceItem.likes,
    });
  };

  updateComments = () => {
    Axios.get(`api/resources/comments/${this.props.resourceItem.id}`)
      .then((result) => {
        console.table(result.data);
        this.setState({
          comments: { comments: result.data },
        });
      })
      .catch((error) => console.log("error getting comments", error));
  };

  // addResourceComment = () => {
  //   //
  // };

  // addResourceLike = () => {
  //   //
  // };

  // Axios.delete(`/api/posts/${event.target.id}`)

  deleteResource = (event) => {
    console.log("In resource delete");
    this.props.dispatch({ type: "DELETE_RESOURCE", payload: event.target.id });
    try {
      this.props.dispatch({ type: "FETCH_RESOURCES" });
    } catch (error) {
      console.log(error);
    }
    this.props.history.push("/resources");
  };

  addResourceComment = (event) => {};

  addResourceLike = (event) => {};

  editResource = (event) => {};

  render() {
    if (this.props.user.id === this.props.resourceItem.user_id) {
      return (
        <Flex width="full" justifyContent="center">
          <Box
            m={4}
            textAlign="center"
            borderWidth={1}
            borderRadius="lg"
            px={4}
            width="full"
            maxWidth="100%"
            textAlign="center"
            boxShadow="lg"
            p={4}
            py={8}
          >
            <Heading as="h3" size="lg">
              {this.props.resourceItem.title}
            </Heading>
            <Text>{this.props.resourceItem.body}</Text>
            <Text>{this.props.resourceItem.upvote_count}</Text>
            <Button
              m={1}
              id={this.props.resourceItem.id}
              onClick={this.addResourceComment}
            >
              Comment
            </Button>
            <Button
              m={1}
              id={this.props.resourceItem.id}
              onClick={this.addResourceLike}
            >
              Like
            </Button>
            <Button
              m={1}
              id={this.props.resourceItem.id}
              onClick={this.editResource}
            >
              Edit
            </Button>
            <Button
              m={1}
              id={this.props.resourceItem.id}
              onClick={this.deleteResource}
            >
              Delete
            </Button>
            <AddComment
              postItem={this.props.resourceItem}
              userID={this.props.user.id}
            />
            {this.state.displayThread ? (
              <>
                <Box>
                  {this.state.comments.comments.map((comment) => {
                    return (
                      <Flex justifyContent="center">
                        <Box
                          textAlign="left"
                          borderWidth={1}
                          borderRadius="lg"
                          px={2}
                          width="full"
                          maxWidth="90%"
                          boxShadow="lg"
                          p={1}
                          py={1}
                          mb={2}
                        >
                          <Text>
                            <Heading as="h5" size="sm">
                              {comment.first_name}:
                            </Heading>{" "}
                            {comment.body}
                          </Text>
                        </Box>
                      </Flex>
                    );
                  })}
                </Box>
                <AddComment
                  postItem={this.props.resourceItem}
                  userID={this.props.user.id}
                />
              </>
            ) : (
              <></>
            )}
          </Box>
        </Flex>
      );
    } else {
      return (
        <div className="postItem">
          <h2>{this.props.resourceItem.title}</h2>
          <p>{this.props.resourceItem.body}</p>
          <p>{this.props.resourceItem.upvote_count}</p>
          <button
            id={this.props.resourceItem.id}
            onClick={this.addResourceComment}
          >
            Comment
          </button>
          <button
            id={this.props.resourceItem.id}
            onClick={this.addResourceLike}
          >
            Like
          </button>
        </div>
      );
    }
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default withRouter(connect(mapStateToProps)(ResourceItem));
