import React, { Component } from "react";
import { Route } from "react-router-dom";
import { Icon, Link } from "@chakra-ui/core";

{
  /* */
}

class ProfileLink extends Component {
  state = {
    profile_url: "",
  };

  componentDidMount = () => {
    this.setState({
      profile_url: this.props.profileLink.profile_url,
    });
  };

  render() {
    const { profileLink } = this.props;
    return (
      <div>
        <Route
          path={`/link/${profileLink.id}`}
          component={() => {
            window.location.href = `${profileLink.profile_url}`;
            return null;
          }}
        />
        <Link href={`/link/${profileLink.id}`} isExternal>
          {profileLink.platform_name} <Icon name="external-link" mx={1} />
        </Link>
        <a target="_blank" href={`${JSON.stringify(profileLink.profile_url)}`}>
          Test
        </a>
        <a
          target="_blank"
          href="https://meetflo.zendesk.com/hc/en-us/articles/230425728-Privacy-Policies"
        >
          Policies
        </a>
      </div>
    );
  }
}

export default ProfileLink;
