import React, { Component } from "react";
import { Box, Textarea, FormControl, Button } from "@chakra-ui/core";
import { connect } from "react-redux";

class Comment extends Component {
  state = {
    comment: { body: "", id: null },
  };

  handleChangeFor = (event) => {
    // Updates our local state when input is changed
    this.setState({
      comment: {
        id: this.props.postItem.id,
        user: this.props.postItem.user_id,
        body: event.target.value,
      },
    });
  };

  handleComment = (event) => {
    this.props.dispatch({ type: "SUBMIT_COMMENT" });
  };
  render() {
    return (
      <div>
        <Box>
          <form>
            <FormControl>
              <Textarea
                mb={4}
                mt={2}
                placeholder="Add a comment"
                isRequired
                value={this.state.comment.body}
                onChange={(event) => this.handleChangeFor(event)}
              />
            </FormControl>
            <Button onClick={this.handleComment()}>Add Comment</Button>
          </form>
        </Box>
      </div>
    );
  }
}

export default connect(Comment);
