  deletePost = (event) => {
    console.log("In delete");
    Axios.delete(`/api/posts/${event.target.id}`)
      .then((result) => this.props.dispatch({ type: "FETCH_POSTS" }))
      .catch((error) => console.log(error));
  };

  render() {
    return (
      <div className="postItem">
        <h2>{this.props.postItem.post_title}</h2>
        <p>{this.props.postItem.post_body}</p>
        <p>{this.props.postItem.likes}</p>
        <button onClick={this.addPostComment}>Comment</button>
        <button onClick={this.addPostLike}>Like</button>
        <button id={postItem.id} onClick={this.deletePost}>
          Delete
        </button>
      </div>
    );
  }
}

export default withRouter(connect()(PostItem));
