import React, { Component } from "react";
import { withRouter } from "react-router";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { connect } from "react-redux";
import {
  Flex,
  Box,
  Button,
  Heading,
  Text,
  IconButton,
  Stack,
} from "@chakra-ui/core";
import AddComment from "../../Comment/AddComment";
import Thread from "../../Thread/PostThread";
import Axios from "axios";

class PostItem extends Component {
  state = {
    comments: 0,
  };

  setDetails = () => {
    this.props.dispatch({
      type: "SET_DETAILS",
      payload: movies,
    }),
      history.push("/details");
  };

  // this.props.dispatch({
  //   type: "FETCH_POST_COMMENTS",
  //   payload: this.props.postItem.id,
  // });

  componentDidMount() {
    Axios.get(`api/posts/comments/${this.props.postItem.id}`)
      .then((result) => {
        console.table(result.data);
        this.setState({
          comments: result.data,
        });
      })
      .catch((error) => console.log("error getting comments", error));
  }

  // addPostLike = () => {
  //   //
  // };

  // Axios.delete(`/api/posts/${event.target.id}`)

  deletePost = (event) => {
    console.log("In delete");
    this.props.dispatch({ type: "DELETE_POST", payload: event.target.id });
    try {
      this.props.dispatch({ type: "FETCH_POSTS" });
    } catch (error) {
      console.log(error);
    }
    this.props.history.push("/");
  };

  addPostComment = (event) => {};

  addPostLike = (event) => {};

  editPost = (event) => {};

  render() {
    if (this.props.user.id === this.props.postItem.user_id) {
      return (
        <div>
          <Router>
            <div>
              <Route
                exact
                path={`/posts/${this.props.postItem.id}`}
                component={Thread}
              />
            </div>
          </Router>
          <Flex width="full" justifyContent="center">
            <Box
              m={4}
              textAlign="center"
              borderWidth={1}
              borderRadius="lg"
              px={4}
              width="full"
              maxWidth="100%"
              boxShadow="lg"
              p={4}
              py={8}
            >
              {/* <div className="postItem"> */}
              <Heading as="h3" size="lg" mb={2}>
                {this.props.postItem.post_title}
              </Heading>
              <Text>By</Text>
              <Heading mb={2} as="h5" size="sm">
                {this.props.postItem.first_name}
              </Heading>
              <Text mb={8}>{this.props.postItem.post_body}</Text>
              <Stack isInline width="full" justifyContent="center">
                <Text p={2}>{this.props.postItem.likes} Likes</Text>
                <Link
                  to={`/posts/${this.props.postItem.id}`}
                  p={2}
                  onClick={() => this.setDetails()}
                >
                  {this.state.comments.length} Comments
                </Link>
              </Stack>
              <Button
                m={1}
                id={this.props.postItem.id}
                onClick={this.addPostLike}
              >
                Like
              </Button>
              <Button m={1} id={this.props.postItem.id} onClick={this.editPost}>
                Edit
              </Button>
              <Button
                m={1}
                id={this.props.postItem.id}
                onClick={this.deletePost}
              >
                Delete
              </Button>
              <AddComment
                postItem={this.props.postItem}
                userID={this.props.user.id}
              />
            </Box>
          </Flex>
        </div>
      );
    } else {
      return (
        <div>
          <Router>
            <div>
              <Route
                exact
                path={`/posts/${this.props.postItem.id}`}
                component={
                  <Thread
                    postItem={this.props.postItem}
                    comments={this.state.comments}
                  />
                }
              />
            </div>
          </Router>
          <Flex width="full" justifyContent="center">
            <Box
              m={4}
              textAlign="center"
              borderWidth={1}
              borderRadius="lg"
              px={4}
              width="full"
              maxWidth="100%"
              boxShadow="lg"
              p={4}
              py={8}
            >
              <Heading as="h3" size="lg">
                {this.props.postItem.post_title}
              </Heading>
              <Text>By</Text>
              <Heading mb={2} as="h5" size="sm">
                {this.props.postItem.first_name}
              </Heading>
              <Stack isInline width="full" justifyContent="center">
                <Text p={2}>{this.props.postItem.likes} Likes</Text>
                <Link to={`/posts/${this.props.postItem.id}`} p={2}>
                  {this.state.comments.length} Comments
                </Link>
              </Stack>
              <Button
                m={1}
                id={this.props.postItem.id}
                onClick={this.addPostLike}
              >
                Like
              </Button>
              <AddComment
                postItem={this.props.postItem}
                userID={this.props.user.id}
              />
            </Box>
          </Flex>
        </div>
      );
    }
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default withRouter(connect(mapStateToProps)(PostItem));
