import React from "react";
import { Link as ReactLink } from "react-router-dom";
// import { Link } from "@chakra-ui/core";
import { connect } from "react-redux";
import LogOutButton from "../LogOutButton/LogOutButton";
import "./Header.css";
import ThemeSelector from "../ThemeSelector/ThemeSelector";
import { Heading, Box, Flex, Stack, Link } from "@chakra-ui/core";

const Nav = (props) => (
  <Box className="nav">
    <Flex width="100%">
      <Box width="100%" textAlign="center" alignItems="center">
        <Link>
          <ReactLink to="/home" />
          <Heading>Emerging Digital Network</Heading>
        </Link>
      </Box>
      <Box>
        <ThemeSelector />
      </Box>
    </Flex>
    <Flex>
      <Stack isInline spacing={2} align="center">
        <Box className="nav-left">
          <Link>
            <ReactLink className="nav-link" to="/home" />
            {props.user.id ? "Home" : "Login / Register"}
          </Link>
        </Box>
      </Stack>
      {props.user.id && (
        <Stack isInline spacing={2} align="center">
          <Box className="whiteBG">
            <Box className="nav-left">
              <Link>
                <ReactLink className="nav-link" to="/resources" />
                Resources
              </Link>
            </Box>
            <Box className="nav-right">
              <Link>
                <ReactLink className="nav-link" to="/profile" />
                Profile
              </Link>
              <LogOutButton className="nav-link" />
            </Box>
          </Box>
        </Stack>
      )}
    </Flex>
  </Box>
);

// Instead of taking everything from state, we just want the user
// object to determine if they are logged in
// if they are logged in, we show them a few more links
// if you wanted you could write this code like this:
// const mapStateToProps = ({ user }) => ({ user });
const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(Nav);
