import React, { Component } from "react";
import { Route } from "react-router-dom";
import { Icon, Link } from "@chakra-ui/core";

{
  /* */
}

class ProfileLink extends Component {
  render() {
    const { profileLink } = this.props;
    return (
      <div>
        <Route
          exact
          path={`/link/${profileLink.id}`}
          component={() => {
            window.location.href = `https://${profileLink.profile_url}`;
            return null;
          }}
        />
        <Link href={`/link/${profileLink.id}`} isExternal>
          {profileLink.platform_name} <Icon name="external-link" mx={1} />
        </Link>
        <a
          target="_blank"
          href={`https://` + JSON.stringify(profileLink.profile_url)}
          params={profileLink.profile_url}
        >
          Test
        </a>
        <a
          target="_blank"
          href="https://meetflo.zendesk.com/hc/en-us/articles/230425728-Privacy-Policies"
        >
          Policies
        </a>
      </div>
    );
  }
}

export default ProfileLink;
