import React from "react";
import {
  Box,
  Heading,
  Textarea,
  FormControl,
  FormLabel,
  Button,
} from "@chakra-ui/core";

Comment = (props) => {
  state = {
    comment: "",
  };

  const handleComment = () => {
    console.log(this.state.comment);
  };

  return (
    <div>
      <Box>
        <form onSubmit={handleComment()}>
          <FormControl>
            <FormLabel>Comments</FormLabel>
            <Textarea onChange={this.setState({ comment: event.target.value })}>
              Add a new comment
            </Textarea>
          </FormControl>
          <Button type="submit">Submit</Button>
        </form>
      </Box>
    </div>
  );
};

export default Comment;
