import React, { Component } from "react";
import { withRouter } from "react-router";

import { connect } from "react-redux";
import Grid from "@material-ui/core/Grid";

class PostItem extends Component {
  // The PostItem component is a child of the PostList

  toDetails = () => {
    this.props.dispatch({
      type: "SET_CURRENT_ID",
      payload: this.props.PostItem.id,
    });
    this.props.dispatch({
      type: "FETCH_DETAIL",
      payload: this.props.PostItem.id,
    });
    this.props.dispatch({
      type: "FETCH_CURRENT_POST",
      payload: this.props.PostItem.id,
    });
    window.setTimeout(this.move, 250); // temp added as a work-around :(
  };
  move = () => {
    this.props.history.push("/Details"); // temp
  };
  render() {
    return (
      <Grid item xs={6} sm={3}>
        <p>{this.props.PostItem.title}</p>
        <div className="PostItem">
          <img
            src={this.props.PostItem.poster}
            alt={this.props.PostItem.title}
            onClick={this.toDetails}
          />
        </div>
      </Grid>
    );
  }
}

export default withRouter(connect()(PostItem));
